import React,{useEffect,useState} from 'react';
import Recipe from './Recipe'
import './App.css';

function App() {
  const APP_ID='a991e8e9'
  const APP_KEY='ec71a96854cd2899a960ca4f669cea88'

  const[recipes,setRecipes]=useState([])

  useEffect(()=>{
    getRecipes()
  },[])

  const getRecipes=async ()=>{
    const response=await fetch(`https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${APP_KEY}&from=0&to=3&calories=591-722&health=alcohol-free`)
    const data=await response.json()
    setRecipes(data.hits)

  }
  return (
    <div className="App">
      <form className='search-form'>
        <input className='search-bar' type="text"/>
        <button className='search-button' type='submit'>Search</button>
      </form>
      {
        recipes.map(recipe=>(
          <Recipe />
        ))
      }
    </div>
  );
}

export default App;
